mapper.select:
SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM ( SELECT t.*, c.chinese_name, c.card_type, c.card_id, p.product_name FROM amount_adjustment t LEFT JOIN basic_customer_information c ON c.id = t.customer_id LEFT JOIN product_attribute p ON p.id = t.product_id WHERE 1=1
and c.chinese_name like '%'||#{customerName}||'%'
AND t.approval = #{approval}
AND t.adjustment_type = #{adjustmentType}
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

SELECT count(*) FROM amount_adjustment t LEFT JOIN basic_customer_information c ON c.id = t.customer_id LEFT JOIN product_attribute p ON p.id = t.product_id WHERE 1=1
and c.CHINESE_NAME like '%'||#{customerName}||'%'
AND t.approval = #{approval}
AND t.adjustment_type = #{adjustmentType}

SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM ( SELECT t.*, c.chinese_name, c.card_type, c.card_id, pa.product_name FROM amount_adjustment t LEFT JOIN basic_customer_information c ON c.id = t.customer_id LEFT JOIN product_attribute pa ON pa.id = t.product_id LEFT JOIN amount_adjustment_process pr ON pr.amount_adjustment_id = t.id WHERE pr.delay_audit_user =#{auditUser}
and c.CHINESE_NAME like '%'||#{customerName}||'%'
AND t.approval = #{approval}
AND t.adjustment_type = #{adjustmentType}
order by t.modified_time desc
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

SELECT count(*) FROM amount_adjustment t LEFT JOIN basic_customer_information c ON c.id = t.customer_id LEFT JOIN product_attribute pa ON pa.id = t.product_id LEFT JOIN amount_adjustment_process pr ON pr.amount_adjustment_id = t.id WHERE pr.delay_audit_user =#{auditUser}
and c.CHINESE_NAME like '%'||#{customerName}||'%'
AND t.approval = #{approval}
AND t.adjustment_type = #{adjustmentType}

mapper.update:
update amount_adjustment_process

	audit_User='',
	audit_Time='',
	delay_audit_user=#{delayAuditUser}

where serial_number=#{serialNumber}

	update amount_adjustment_process t set t.delay_audit_user = ''
	where t.amount_adjustment_id in(
			select ad.id from amount_adjustment ad 
			where ad.approval='Audit'
         and ad.modified_time<sysdate - #{maxDay}
		)
