mapper.select:
SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM ( SELECT k.* FROM ( SELECT M .*, s.DISPLAY_NAME as MANAGER_NAME, s1.DISPLAY_NAME AS ORIGINATOR_NAME, (case when m.ORIGINATOR = #{originator} then 1 else 2 end) kk FROM MANAGER_RISK_WARNING M INNER JOIN SYS_USER s ON M .CUSTOMER_MANAGER_ID = s. ID INNER JOIN SYS_USER s1 ON M .originator = s1. ID WHERE 1=1
AND M .ORIGINATOR = #{originator}
) K where 1=1
AND k.MANAGER_NAME =#{managerName}
AND k.ORIGINATOR_NAME =#{originatorName}
ORDER BY K .kk
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM ( SELECT k.* FROM ( SELECT M .*, s.DISPLAY_NAME as MANAGER_NAME, s1.DISPLAY_NAME AS ORIGINATOR_NAME, (case when m.ORIGINATOR = #{originator} then 1 else 2 end) kk FROM MANAGER_RISK_WARNING M INNER JOIN SYS_USER s ON M.CUSTOMER_MANAGER_ID = s.ID INNER JOIN SYS_USER s1 ON M.originator=s1.ID WHERE 1=1
AND M.CUSTOMER_MANAGER_ID = #{customerManagerId}
) K where 1=1
AND k.MANAGER_NAME =#{managerName}
AND k.ORIGINATOR_NAME =#{originatorName}
ORDER BY K.kk
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

SELECT COUNT (*) FROM ( SELECT k.* FROM ( SELECT M.*, s.DISPLAY_NAME as MANAGER_NAME, s1.DISPLAY_NAME AS ORIGINATOR_NAME FROM MANAGER_RISK_WARNING M INNER JOIN SYS_USER s ON M .CUSTOMER_MANAGER_ID = s.ID INNER JOIN SYS_USER s1 ON M.originator = s1.ID WHERE 1=1
AND M.ORIGINATOR = #{originator}
) K where 1=1
AND k.MANAGER_NAME =#{managerName}
AND k.ORIGINATOR_NAME =#{originatorName}
)

SELECT COUNT (*) FROM ( SELECT k.* FROM ( SELECT M .*, s.DISPLAY_NAME as MANAGER_NAME, s1.DISPLAY_NAME AS ORIGINATOR_NAME FROM MANAGER_RISK_WARNING M INNER JOIN SYS_USER s ON M .CUSTOMER_MANAGER_ID = s. ID INNER JOIN SYS_USER s1 ON M .originator = s1. ID WHERE 1=1
AND M .CUSTOMER_MANAGER_ID = #{customerManagerId}
) K where 1=1
AND k.MANAGER_NAME =#{managerName}
AND k.ORIGINATOR_NAME =#{originatorName}
)

mapper.update:
	
		UPDATE MANAGER_RISK_WARNING M
	    SET M .FEEDBACK = #{feedback}
	    ,m.RETURN_RECEIPT='1',
	     m.FEEDBACK_TIME= SYSDATE
  		  WHERE
		M ."ID" = #{riskId}
