mapper.select:
SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM ( SELECT npls.*, c.chinese_name, c.card_id, cai.account_status, cai.ACCOUNT_NUMBER, cai.OVERDUE_AMOUNT, cai.OVERDUE_TIME, cai.CARD_NUMBER FROM npls_information npls LEFT JOIN basic_customer_information c ON c.id = npls.customer_id LEFT JOIN customer_account_information cai ON cai.id = npls.account_id WHERE 1=1
and c.CHINESE_NAME like '%'||#{customerName}||'%'
AND npls.CREATE_METHOD=#{createMethod}
AND npls.VERIFICATION_TYPE = #{verificationType}
AND npls.REVIEW_RESULTS=#{reviewResults}
AND npls.VERIFICATION_STATUS = #{verificationStatus}
order by npls.created_time desc
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

SELECT count(*)
  FROM  npls_information npls
 LEFT JOIN basic_customer_information c
    ON c.id = npls.customer_id
  WHERE 1=1
 
	 and c.CHINESE_NAME like '%'||#{customerName}||'%'
 
 
	AND npls.CREATE_METHOD=#{createMethod}
 
 
	AND npls.VERIFICATION_TYPE = #{verificationType}
 
 
	AND npls.REVIEW_RESULTS=#{reviewResults}
 
 
	AND npls.VERIFICATION_STATUS = #{verificationStatus}

SELECT count(*)
  FROM  npls_information npls
  WHERE 
  customer_id = #{customerId} 
  and account_id = #{accountId}
  and review_results in ('new','confirm')
