mapper.select:
select count(*)
from collection_plan t
where t.customer_id=#{customerId}
and t.product_id=#{productId}
and (t.end_result=#{endResult1} or t.end_result=#{endResult2} or t.end_result is null)

SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM ( SELECT c.*, P.PRODUCT_NAME, i.CHINESE_NAME, u.display_name,(select count(1) from collection_plans_action a where a.collection_plan_id=c.id) as countAction FROM COLLECTION_PLAN c LEFT JOIN PRODUCT_ATTRIBUTE P ON c.product_id = P.ID LEFT JOIN BASIC_CUSTOMER_INFORMATION i ON c.customer_id = i.ID LEFT JOIN SYS_USER u ON i.user_id=u.id where i.user_id in
#{item.userId}
order by c.created_time desc
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

select count(*) from COLLECTION_PLAN where customer_manager_id=#{customerManagerId} and end_result=#{result}
and created_time<to_date(#{startTime},'yyyy-mm-dd hh24:mi:ss')
and collection_endtime>to_date(#{endTime},'yyyy-mm-dd hh24:mi:ss')
