mapper.select:
SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM ( SELECT risk.*, customer.chinese_name as customerName, customer.card_type as cardType, customer.card_id as cardId, sysuser.display_name as reportedManager FROM basic_customer_information customer INNER JOIN risk_list risk ON customer.id = risk.customer_id INNER JOIN sys_user sysuser ON sysuser.id = risk.reported_id_manager WHERE 1=1
AND risk.reported_id_manager=#{reportedIdManager}
AND risk.status=#{status}
AND (risk.status='REPORTED_CARDCENTER' or risk.status='CONFIRMED_CARDCENTER' or risk.status='REJECT_CARDCENTER')
AND risk.risk_create_type=#{riskCreateType}
AND customer.chinese_name like '%${customerName}%'
AND customer.card_type=#{cardType}
AND customer.card_id like '%${cardId}%'
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

SELECT count(*)
  FROM basic_customer_information customer
 INNER JOIN risk_list risk
    ON customer.id = risk.customer_id
  WHERE 1=1 
 
	AND risk.reported_id_manager=#{reportedIdManager}
 
 
	AND risk.status=#{status}
 
 
 
 
	AND (risk.status='REPORTED_CARDCENTER' or risk.status='CONFIRMED_CARDCENTER' or risk.status='REJECT_CARDCENTER')
 
 
	AND risk.risk_create_type=#{riskCreateType}
 
 
	AND customer.chinese_name like '%${customerName}%'
 
 
	AND customer.card_type=#{cardType}
 
 
	AND customer.card_id like '%${cardId}%'
 
 ORDER BY risk.created_time DESC

SELECT count(*)from( select tt.*,c.num_risk as numRisk ,b.chinese_name as customerName,b.card_type as cardType,b.card_id as cardId,a.risk_des as riskDescription from (select * from (select RISK_LIST.*, row_number() over(partition by customer_id order by risk_level desc) cn from RISK_LIST) where cn = 1) tt, (select max(t.risk_level) as aa, count(0) as num_risk, t.customer_id from RISK_LIST t group by t.customer_id) c ,basic_customer_information b ,risk_attribute a where tt.customer_id=c.customer_id and b.id=c.customer_id and a.risk_attribute=tt.risk_attribute and tt.status ='CONFIRMED_CARDCENTER'
AND b.chinese_name like '%${customerName}%'
AND b.card_type=#{cardType}
AND b.card_id like '%${cardId}%'
)bb
